mapper.insert:
INSERT SHOP_USER (
USER_ID
, USER_PW
, USER_NAME
, USER_EMAIL
, USER_TEL
) VALUES(
#{userId}
, #{userPw}
, #{userName}
, #{userEmail}
, #{userTel}
)

mapper.select:
SELECT USER_ID
      ,USER_TEL
FROM SHOP_USER
WHERE USER_ID = #{userId}
      , USER_TEL = #{userTel}

SELECT USER_ID
      , USER_PW
FROM SHOP_USER
WHERE USER_ID = #{userId}
      , USER_PW = #{userPw}
